import * as React from "react";
import Box from "@mui/material/Box";
import Container from "@mui/material/Container";
import CssBaseline from "@mui/material/CssBaseline";
import SearchAppBar from '../../common/navbar/navbar';
import ToggleButton from '@mui/material/ToggleButton';
import ToggleButtonGroup from '@mui/material/ToggleButtonGroup';
import Grid from '@mui/material/Grid';
import { createTheme, ThemeProvider } from "@mui/material/styles";

const defaultTheme = createTheme();

export default function ProductDetail() {

    const [alignment, setAlignment] = React.useState('web');
    const [age, setAge] = React.useState('');

    const handleChange1 = (event) => {
      setAge(event.target.value);
    };  

    const handleChange = (event, newAlignment) => {
      setAlignment(newAlignment);
    };  

    const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    console.log({
      email: data.get("email"),
      password: data.get("password")
    });
  };

  return (
    <ThemeProvider theme={defaultTheme}>
      <SearchAppBar/>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            minWidth: "max-content",
            marginTop: 5,
            display: "flex",
            flexDirection: "column",
            alignItems: "center"
          }}
        >
        <ToggleButtonGroup
      value={alignment}
      exclusive
      onChange={handleChange}
      aria-label="Platform"
    >
      <ToggleButton value="web">All</ToggleButton>
      <ToggleButton value="android">Apparel</ToggleButton>
      <ToggleButton value="ios">Electronics</ToggleButton>
      <ToggleButton value="ios1">Personal Care</ToggleButton>
    </ToggleButtonGroup>
    </Box>
      </Container>
      <Grid sx={{margin: "2%"}} justifyContent="center" alignItems="center" container spacing={2}>
        <Grid item xs={3}>
            <img style={{maxWidth: "-webkit-fill-available", borderRadius: "5%"}} src="https://abdelghafour122.github.io/ecommerce-product-page/static/media/image-product-1.520cc50bd13955f55cb2.jpg" alt="product-thumbnail" />
        </Grid>
        <Grid item xs={5}>
            <Grid sx={{margin: "2%"}} justifyContent="center" alignItems="start" container spacing={2}>
                <Grid item xs={8}>
                    dfssss
                </Grid>
                <Grid item xs={8}>
                    1dfssss
                </Grid>
            </Grid>
        </Grid>
      </Grid>

    </ThemeProvider>
  );
}
