import returnProducts from '../../models/products';

const initialState = returnProducts();

export default function productReducer(state = initialState, action) {
    switch (action.type) {
      case 'db/productAdded': {
        let { order } = action.payload;
        return state.concat(order);
      }
      case 'db/productModified': {
        state[(action.payload.id - 1)] = action.payload;
        return state;
      }
      case 'db/productDeleted': {
        return state.splice((action.payload.id - 1), 1);
      }
      default:
        return state;
    }
}
