                <Grid item xs={8}>
                    <TextField
                    margin="normal"
                    required
                    fullWidth
                    name="quantity"
                    label="Quantity"
                    id="quantity"
                    autoComplete="quantity"
                    />
                </Grid>
                <Grid item xs={8}>
                <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    sx={{ maxWidth: "40%", mt: 3, mb: 2 }}
                        >
                        Place Order
                    </Button>
                </Grid>
            </Grid>
        </Grid>
      </Grid>        
      </CardContent>
    </Card>
    </ThemeProvider>
  );
}
